class MyNTC {
public:
    MyNTC(uint8_t pin, uint32_t resistance, uint16_t beta, uint32_t resistance_support = 10000, bool isESP32 = true) :
    _pin(pin), _resistance(resistance), _beta(beta),  _resistance_support(resistance_support), _isESP32(isESP32) {
      if (isESP32) {
        adcMax = 4095.0; // ADC 12-бит (0-4095)
        Vs = 3.3;        
      }
      else {
        adcMax = 1023.0; // ADC  10-бит (0-1023)
        Vs = 5.0; 
      }
    }
 
    // прочитать температуру с пина
    float getTemp() {
        return computeTemp(analogRead(_pin));
    }
    float getTempDenoised() {
        return simpleKalman(computeTemp(analogRead(_pin)));
    }
        
    float computeTemp(float adc = 0) {
        float Vout, Rt = 0;
        float T, Tc = 0;
        if (_isESP32) adc = ADC_LUT[(int)adc];
        Vout = adc * Vs/adcMax;
        Rt = _resistance_support * Vout / (Vs - Vout);
        T = 1/(1.0/(25 + 273.15) + log(Rt/_resistance)/_beta);   
        Tc = T - 273.15;
        return Tc;
    }
    
private:    
    const uint8_t _pin = 0;
    const uint16_t _beta = 0;
    const uint32_t _resistance = 10000;
    const uint32_t _resistance_support = 10000;
    const bool _isESP32 = true;
    float adcMax = 4095.0;
    float Vs = 3.3;
    
    float _err_measure = 0.8;  // примерный шум измерений
    float _q = 0.8;   // скорость изменения значений 0.001-1, варьировать самому
    
    float simpleKalman(float newVal) {
      float _kalman_gain, _current_estimate;
      static float _err_estimate = _err_measure;
      static float _last_estimate;
      _kalman_gain = (float)_err_estimate / (_err_estimate + _err_measure);
      _current_estimate = _last_estimate + (float)_kalman_gain * (newVal - _last_estimate);
      _err_estimate =  (1.0 - _kalman_gain) * _err_estimate + fabs(_last_estimate - _current_estimate) * _q;
      _last_estimate = _current_estimate;
      return _current_estimate;
    }
};
